import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import StudentForm from "./components/StudentForm";
import StudentList from "./components/StudentList";
import './App.css';
import cubesVideo from './videos/cubes.mp4';
import backgroundMusic from './audio/background-music.mp3';
import { neonCursor } from 'https://unpkg.com/threejs-toys@0.0.8/build/threejs-toys.module.cdn.min.js';

const App = () => {
    const [students, setStudents] = useState([]);
    const audioRef = useRef(null);
    const [animate, setAnimate] = useState(false);

    useEffect(() => {
        // Initialize neon cursor
        neonCursor({
            el: document.getElementById('neon-cursor'),
            shaderPoints: 16,
            curvePoints: 80,
            curveLerp: 0.5,
            radius1: 5,
            radius2: 30,
            velocityTreshold: 10,
            sleepRadiusX: 100,
            sleepRadiusY: 100,
            sleepTimeCoefX: 0.0025,
            sleepTimeCoefY: 0.0025
        });

        fetchStudents();

        if (!localStorage.getItem("visited")) {
            setAnimate(true);
            localStorage.setItem("visited", "true");
            setTimeout(() => {
                setAnimate(false);
            }, 1000);
        }
    }, []);

    useEffect(() => {
        if (audioRef.current) {
            audioRef.current.volume = 0.1;
        }
    }, []);

    const fetchStudents = async () => {
        try {
            const response = await axios.get("http://127.0.0.1:5000/api/students");
            setStudents(response.data);
        } catch (error) {
            console.error("Error fetching students:", error);
        }
    };

    const addStudent = async (name, course) => {
        try {
            await axios.post("http://127.0.0.1:5000/api/students", { name, course });
            fetchStudents();
        } catch (error) {
            console.error("Error adding student:", error);
        }
    };

    const deleteStudent = async (studentId) => {
        try {
            await axios.delete(`http://127.0.0.1:5000/api/students/${studentId}`);
            fetchStudents();
        } catch (error) {
            console.error("Error deleting student:", error);
        }
    };

    return (
        <div id="app" className={`container ${animate ? "pop-animate" : ""}`}>
            {/* Neon Cursor Background */}
            <div id="neon-cursor" style={{ position: "absolute", top: 0, left: 0, width: "100%", height: "100%", zIndex: 0 }}></div>

            {/* Video Background */}
            <video autoPlay loop muted className="background-video" style={{ zIndex: 1 }}>
                <source src={cubesVideo} type="video/mp4" />
                Your browser does not support the video tag.
            </video>

            {/* Main Content */}
            <audio ref={audioRef} autoPlay loop>
                <source src={backgroundMusic} type="audio/mpeg" />
                Your browser does not support the audio element.
            </audio>
            <div className="snake-border" style={{ zIndex: 2 }}>
                <svg
                    className="snake-svg"
                    viewBox="0 0 100 100"
                    preserveAspectRatio="none"
                    style={{
                        position: "absolute",
                        top: 0,
                        left: 0,
                        width: "100%",
                        height: "100%",
                        zIndex: 1,
                    }}
                >
                    <rect className="snake-rect" x="0" y="0" width="100" height="100" rx="5" />
                </svg>
                <div
                    className="overlay"
                    style={{
                        position: "relative",
                        zIndex: 3,
                        padding: "20px",
                        textAlign: "center",
                    }}
                >
                    <h1 className="srs-head">Student Recording System</h1>
                    <StudentForm addStudent={addStudent} />
                    <StudentList students={students} deleteStudent={deleteStudent} />
                </div>
            </div>
        </div>
    );
};

export default App;
