import React, { useState, useEffect } from "react";
import axios from "axios";
import StudentForm from "./components/StudentForm";
import StudentList from "./components/StudentList";
import './App.css';
import cubesVideo from './videos/cubes.mp4';
import backgroundMusic from './audio/background-music.mp3';

const App = () => {
  const [students, setStudents] = useState([]);

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:5000/api/students");
      setStudents(response.data);
    } catch (error) {
      console.error("Error fetching students:", error);
    }
  };

  const addStudent = async (name, course) => {
    try {
      await axios.post("http://127.0.0.1:5000/api/students", { name, course });
      fetchStudents();
    } catch (error) {
      console.error("Error adding student:", error);
    }
  };

  const deleteStudent = async (studentId) => {
    try {
      await axios.delete(`http://127.0.0.1:5000/api/students/${studentId}`);
      fetchStudents();
    } catch (error) {
      console.error("Error deleting student:", error);
    }
  };

  return (
    <div className="container">
      <video autoPlay loop muted className="background-video">
        <source src={cubesVideo} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <audio autoPlay loop>
        <source src={backgroundMusic} type="audio/mpeg" />
        Your browser does not support the audio element.
      </audio>
      <div className="snake-border">
        <svg
          className="snake-svg"
          viewBox="0 0 100 100"
          preserveAspectRatio="none"
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            zIndex: 1,
          }}
        >
          <rect className="snake-rect" x="0" y="0" width="100" height="100" rx="5"/>
        </svg>
        <div
          className="overlay"
          style={{
            position: "relative",
            zIndex: 2,
            padding: "20px",
            textAlign: "center",
          }}
        >
          <h1 className="srs-head">Student Recording System</h1>
          <StudentForm addStudent={addStudent} />
          <StudentList students={students} deleteStudent={deleteStudent} />
        </div>
      </div>
    </div>
  );
};

export default App;
